function showHideDiv(id1,id){
			//var DivId="#"+id;
	    	$("#contactDivId,#aboutDivId,#faqDivId,#termDivID,#privacyDivID,#homeDivId").hide();	    	
	    	$("#"+id).show();
	    	$("#hedHomeId,#hedAbtId,#hedFaqId,#hedContactId").removeClass("active");
	    	$("#"+id1).addClass("active");
	    	localStorage.setItem('activeClassId',id1);
	    	localStorage.setItem('show',id);
}
function createCookie(name,value,days) {
	if (days) {
		var date = new Date();
		date.setTime(date.getTime()+(days*24*60*60*1000));
		var expires = "; expires="+date.toGMTString();
	}
	else var expires = "";
	document.cookie = name+"="+value+expires+"; path=/";
}

function readCookie(name) {
	var nameEQ = name + "=";
	var ca = document.cookie.split(';');
	for(var i=0;i < ca.length;i++) {
		var c = ca[i];
		while (c.charAt(0)==' ') c = c.substring(1,c.length);
		if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
	}
	return null;
}

function eraseCookie(name) {
	createCookie(name,"",-1);
}	

function changeLanguage() {
userLoginForm.action = "UserLogin.do?method=changeLanguage";
userLoginForm.submit();
}

function md5Auth() {
	if (formValidate()) {
		document.userLoginForm.action = "login.html";
		return true;
	}
	return false;
	}

function verifyPassword() {
userLoginForm.action = "UserLogin.do?method=verifyPassword";
userLoginForm.submit();
return true;

}

function formValidate() {
if (document.userLoginForm.userid.value == "" || document.userLoginForm.password.value == "") {
	alert('Please Enter User ID and Password');
	$('#userid').focus();
	return false;
	
}
return true;
}



function onCancel() {
document.userLoginForm.userid.value = "";
document.userLoginForm.password.value = "";
document.userLoginForm.language.value = "resource";
}
function makeThemeAndLanguageSelected()
{
	 var selectedTheme = document.getElementById("switchcontrol");
		var selectedThemeValue = document.getElementById("selectedTheme").innerHTML;
		if(selectedThemeValue==""){
			selectedThemeValue='default';
			document.getElementById("selectedTheme").innerHTML='default';
		}
		//alert("selectedThemeValue:-"+selectedThemeValue);
		if(selectedTheme != undefined)
		selectedTheme.value=selectedThemeValue;		
}
function changeViewAll(x)
{
	if(x==1){
		$("#aboutDivId").show();
		$("#homeDivId").hide();
	}else if(x==2){
		$("#faqDivId").show();
		$("#homeDivId").hide();
	}
	
}

function submitRlbUlbForm()
